            "schema": { "type": "string", "example": "USD" },
            "description": "Currency code for compensation normalization (e.g. USD, EUR)."
          },
          {
            "name": "periodicity",
            "in": "query",
            "required": false,
            "schema": {
              "type": "string",
              "enum": ["hourly", "monthly", "yearly"]
