                  { "keywords": { "term": "Designer", "locale": "en" } },
                  { "language": { "term": "English", "fluency": "fully-fluent" } },
                  { "skill/role": { "text": "Design systems", "proficiency": "expert" } },
                  { "skill/role": { "text": "Product design", "proficiency": "expert" } },
                  { "status": { "code": "open" } }
                ]

