import type { SearchResponse } from "@torre-swipe/types";
import { logger } from "./logger";

const API_URL = process.env.NEXT_PUBLIC_API_URL ?? "http://localhost:3001";

export type FetchOpportunitiesParams = {
  size?: number;
  keywords?: string;
  currency?: string;
  periodicity?: string;
};

